import React, { useState, useEffect, useRef } from 'react';
import { ThemeProvider, Frame, List, TitleBar, Button as React95Button } from '@react95/core';
import styled, { keyframes } from 'styled-components';
import GlobalStyles from './GlobalStyles';

// Boot Screen Component
const BootScreen = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: #1e1e1e;  // Dark background
  color: #ffffff;  // Light text
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: 'OMORI_GAME', monospace;
  font-size: 24px;
  z-index: 999;
  opacity: ${({ isVisible }) => (isVisible ? 1 : 0)};
  transition: opacity 2s ease-out;
  pointer-events: ${({ isVisible }) => (isVisible ? 'all' : 'none')};
`;

// Content Container Component
const ContentContainer = styled.div`
  background-color: #1e1e1e;  // Dark background
  opacity: ${({ opacity }) => opacity};
  transition: opacity 2s ease-in;
  width: 100vw;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  visibility: ${({ opacity }) => (opacity > 0 ? 'visible' : 'hidden')};
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

// Desktop Component
const Desktop = styled.div`
  background-color: #1e1e1e;  // Dark background
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

// Window Border Component
const WindowBorder = styled.div`
  padding: 8px;
  box-sizing: border-box;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 3px solid #606060;  // Adjust as needed
  height: 90vh;
  width: 80vw;
  background-color: #2e2e2e;  // Darker background for windows
`;

// Windowed Environment Component
const WindowedEnvironment = styled.div`
  position: relative;
  background: linear-gradient(0deg, rgba(33,5,77,1) 0%, rgba(42,8,129,1) 30%, rgba(108,14,255,1) 100%);
  height: 100%;
  width: 100%;
  box-sizing: border-box;
`;

// Slide and Zoom Animation
const slideZoomAnimation = keyframes`
  0% {
    transform: translateX(0) scale(1.5); // Start position and scale (150% zoomed in)
  }
  50% {
    transform: translateX(-50%) scale(1.5); // Keep the zoom in while sliding to the left
  }
  100% {
    transform: translateX(-100%) scale(1.5); // End position with the same scale
  }
`;

const SlidingBackgroundStars = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  animation: slide 30s linear infinite; // Change to 30 seconds
  background-image: url("/images/wallpaper.png"); // Initial wallpaper
  background-size: 120%;
  background-repeat: repeat;
  image-rendering: high-quality;
  opacity: 1;
  overflow: hidden;
  z-index: 0;

  @keyframes slide {
    0% {
      background-position-x: 0%;
    }
    100% {
      background-position-x: 100%;
    }
  }
`;

// Taskbar Component
const Taskbar = styled.div`
  background: #3a3a3a;  // Dark taskbar
  height: 40px;
  width: 100%;
  position: absolute;
  bottom: 0;
  display: flex;
  align-items: center;
  padding: 0 10px;
  box-sizing: border-box;
  box-shadow: inset 1px 1px #fff, inset -1px -1px #888;
`;

// Styled React95 Button Component
const StyledReact95Button = styled(React95Button)`
  && {
    margin-right: 8px;
    font-family: 'OMORI_GAME', monospace;
    font-size: 16px;
    height: 30px;
    background-color: #3a3a3a;  // Dark button background
    border-color: #3a3a3a;  // Dark button border
    box-shadow: 1px 1px #888, -1px -1px #fff;

    ${({ isActive }) =>
        isActive &&
        `
        background-color: #5a5a5a;  // Active button background
        border-color: #5a5a5a;
        box-shadow: inset 2px 2px #888, inset -2px -2px #fff;
      `}

    &:focus {
      outline: none;
    }
  }
`;

// Clock Component
const Clock = styled.div`
  margin-left: auto;
  font-family: 'OMORI_GAME', monospace;
  font-size: 16px;
  color: #ffffff;  // Light clock text
  text-align: center;
  padding: 2px 5px;
  background: #3a3a3a;  // Dark clock background
  box-shadow: inset 1px 1px #888, inset -1px -1px #fff;
  user-select: none;
`;

// Draggable Frame Component
const DraggableFrame = styled(Frame)`
  cursor: move;
  position: absolute;
  display: ${({ isVisible }) => (isVisible ? 'block' : 'none')};
`;

// About Me Content Component
const AboutMeContent = () => (
    <div>
      <h1 style={{ color: '#ffffff' }}>About Me</h1>
      Hey, names 이스터!<br />
      Im that weird Korean nerd that produces music, watches anime,<br />
      sweats destiny 2, edits videos, draws, and codes (clearly).<br />
      Feel free to stay and chill here!
      <hr style={{ borderColor: '#555555' }} />
      <div style={{ display: 'flex', marginTop: '20px', alignItems: 'center', justifyContent: 'flex-start' }}>
        <a href="https://github.com/eastercreeper" target="_blank" rel="noopener noreferrer">
          <img src="/images/gh.png" alt="GitHub Logo" style={{ width: '15px', height: '15px', marginRight: '10px' }} />
        </a>
        <a href="https://github.com/eastercreeper" target="_blank" rel="noopener noreferrer">
          <img src="/images/yt.png" alt="YouTube Logo" style={{ width: '15px', height: '15px', marginRight: '10px' }} />
        </a>
      </div>
    </div>
);

// My Portfolio Content Component
const MyPortfolioContent = () => (
    <div>
      <h1 style={{ color: '#ffffff' }}>Extra Window</h1>
      <p style={{ color: '#ffffff' }}>Test</p>
    </div>
);

// App Component
const App = () => {
  const environmentRef = useRef(null);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString());
  const [isStartMenuVisible, setStartMenuVisible] = useState(false);
  const [zIndexCounter, setZIndexCounter] = useState(1);
  const [activeWindow, setActiveWindow] = useState('');
  const [windows, setWindows] = useState({
    aboutMe: { isVisible: false, position: { x: 100, y: 100 }, zIndex: 1 },
    extraWindow: { isVisible: false, position: { x: 200, y: 150 }, zIndex: 1 },
  });
  const [bootScreenVisible, setBootScreenVisible] = useState(true);
  const [bootMessage, setBootMessage] = useState("Boot up your laptop.");
  const audioRef = useRef(new Audio('/sfx/boot.mp3'));
  const backgroundMusicRef = useRef(new Audio('/sfx/lostnfound.mp3'));
  const [contentOpacity, setContentOpacity] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date().toLocaleTimeString()), 1000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    audioRef.current.onended = () => {
      setBootScreenVisible(false);
      setContentOpacity(1);
      backgroundMusicRef.current.play();
    };
    audioRef.current.play();
  }, []);

  const toggleStartMenu = () => setStartMenuVisible((prev) => !prev);

  const openWindow = (windowName) => {
    setWindows((prev) => ({
      ...prev,
      [windowName]: { ...prev[windowName], isVisible: true, zIndex: zIndexCounter },
    }));
    setZIndexCounter((prev) => prev + 1);
    setActiveWindow(windowName);
  };

  const closeWindow = (windowName) => {
    setWindows((prev) => ({
      ...prev,
      [windowName]: { ...prev[windowName], isVisible: false },
    }));
  };

  const handleDrag = (e, windowName) => {
    const { clientX, clientY } = e;
    setWindows((prev) => ({
      ...prev,
      [windowName]: {
        ...prev[windowName],
        position: { x: clientX - 50, y: clientY - 20 }, // Centering the window
      },
    }));
  };

  return (
      <ThemeProvider>
        <GlobalStyles />
        <SlidingBackgroundStars />
        <BootScreen isVisible={bootScreenVisible}>
          {bootMessage}
        </BootScreen>
        <ContentContainer opacity={contentOpacity}>
          <Desktop>
            <WindowedEnvironment ref={environmentRef}>
              {windows.aboutMe.isVisible && (
                  <DraggableFrame
                      isVisible={windows.aboutMe.isVisible}
                      title="About Me"
                      style={{ left: windows.aboutMe.position.x, top: windows.aboutMe.position.y, zIndex: windows.aboutMe.zIndex }}
                      onDrag={(e) => handleDrag(e, 'aboutMe')}
                  >
                    <AboutMeContent />
                    <React95Button onClick={() => closeWindow('aboutMe')}>Close</React95Button>
                  </DraggableFrame>
              )}
              {windows.extraWindow.isVisible && (
                  <DraggableFrame
                      isVisible={windows.extraWindow.isVisible}
                      title="Extra Window"
                      style={{ left: windows.extraWindow.position.x, top: windows.extraWindow.position.y, zIndex: windows.extraWindow.zIndex }}
                      onDrag={(e) => handleDrag(e, 'extraWindow')}
                  >
                    <MyPortfolioContent />
                    <React95Button onClick={() => closeWindow('extraWindow')}>Close</React95Button>
                  </DraggableFrame>
              )}
            </WindowedEnvironment>
          </Desktop>
          <Taskbar>
            <StyledReact95Button onClick={() => openWindow('aboutMe')}>About Me</StyledReact95Button>
            <StyledReact95Button onClick={() => openWindow('extraWindow')}>My Portfolio</StyledReact95Button>
            <Clock>{currentTime}</Clock>
          </Taskbar>
        </ContentContainer>
      </ThemeProvider>
  );
};

export default App;
