import React, { useState, useEffect, useRef } from 'react';
import { ThemeProvider, Frame, List, TitleBar, Button as React95Button } from '@react95/core';
import styled, { keyframes } from 'styled-components';
import GlobalStyles from './GlobalStyles';

// Boot Screen Component
const BootScreen = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: #1e1e1e;  // Dark background
  color: #ffffff;  // Light text
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: 'OMORI_GAME', monospace;
  font-size: 24px;
  z-index: 999;
  opacity: ${({ isVisible }) => (isVisible ? 1 : 0)};
  transition: opacity 2s ease-out;
  pointer-events: ${({ isVisible }) => (isVisible ? 'all' : 'none')};
`;

// Content Container Component
const ContentContainer = styled.div`
  background-color: #1e1e1e;  // Dark background
  opacity: ${({ opacity }) => opacity};
  transition: opacity 2s ease-in;
  width: 100vw;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  visibility: ${({ opacity }) => (opacity > 0 ? 'visible' : 'hidden')};
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

// Desktop Component
const Desktop = styled.div`
  background-color: #1e1e1e;  // Dark background
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

// Window Border Component
const WindowBorder = styled.div`
  padding: 8px;
  box-sizing: border-box;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 3px solid #606060;  // Adjust as needed
  height: 90vh;
  width: 80vw;
  background-color: #2e2e2e;  // Darker background for windows
`;

// Windowed Environment Component
const WindowedEnvironment = styled.div`
  position: relative;
  background: linear-gradient(0deg, rgba(33,5,77,1) 0%, rgba(42,8,129,1) 30%, rgba(108,14,255,1) 100%);
  height: 100%;
  width: 100%;
  box-sizing: border-box;
`;

// Slide Animation for Background
const slideAnimation = keyframes`
  0% {
    background-position: 0%;
    opacity: 1;
  }
  80% {
    opacity: 1;
  }
  100% {
    background-position: 100%;
    opacity: 0;
  }
`;

// Fade-in Animation
const fadeInAnimation = keyframes`
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
`;
// Sliding Background Stars Component
const SlidingBackgroundStars = () => {
  const [backgroundImage, setBackgroundImage] = useState("");
  const [isFading, setIsFading] = useState(false);
  const randombgs = [
    "/images/wallpaper.png",
    "/images/wallpaper1.png",
    "/images/wallpaper2.png",
    "/images/wallpaper3.png"
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const changeBackground = () => {
      // Start fading out to 25%
      setIsFading(true);

      // After fade-out duration, change the background
      setTimeout(() => {
        // Update index to the next wallpaper, looping back if needed
        setCurrentIndex((prevIndex) => (prevIndex + 1) % randombgs.length);
        // Set the new background image
        setBackgroundImage(randombgs[currentIndex]); // Change background image

        // Immediately start fading back in
        setIsFading(false); // Start fading back in
      }, 2000); // Wait for 2 seconds to fade out
    };

    // Start the first change immediately
    changeBackground();

    const interval = setInterval(() => {
      // Call changeBackground every 30 seconds (30000 ms)
      changeBackground();
    }, 30000); // 30000 ms for interval

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [currentIndex]);

  return (
      <StyledBackground backgroundImage={backgroundImage} isFading={isFading} />
  );
};

// Styled Background Component
const StyledBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url(${props => props.backgroundImage});
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  z-index: 0;
  opacity: ${({ isFading }) => (isFading ? 0.25 : 1)}; // Fade to 25% opacity when fading out
  transition: opacity 2s ease-in-out; // Transition for fade in/out
`;



