import React, { useState, useEffect, useRef } from 'react';
import { ThemeProvider, Frame, List, TitleBar, Button as React95Button } from '@react95/core';
import styled, { keyframes } from 'styled-components';
import GlobalStyles from './GlobalStyles';

// Boot Screen Component
const BootScreen = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: #1e1e1e;  // Dark background
  color: #ffffff;  // Light text
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: 'OMORI_GAME', monospace;
  font-size: 24px;
  z-index: 999;
  opacity: ${({ isVisible }) => (isVisible ? 1 : 0)};
  transition: opacity 2s ease-out;
  pointer-events: ${({ isVisible }) => (isVisible ? 'all' : 'none')};
`;

// Content Container Component
const ContentContainer = styled.div`
  background-color: #1e1e1e;  // Dark background
  opacity: ${({ opacity }) => opacity};
  transition: opacity 2s ease-in;
  width: 100vw;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  visibility: ${({ opacity }) => (opacity > 0 ? 'visible' : 'hidden')};
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

// Desktop Component
const Desktop = styled.div`
  background-color: #1e1e1e;  // Dark background
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

// Window Border Component
const WindowBorder = styled.div`
  padding: 8px;
  box-sizing: border-box;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 3px solid #606060;  // Adjust as needed
  height: 90vh;
  width: 80vw;
  background-color: #2e2e2e;  // Darker background for windows
`;

// Windowed Environment Component
const WindowedEnvironment = styled.div`
  position: relative;
  background: linear-gradient(0deg, rgba(33,5,77,1) 0%, rgba(42,8,129,1) 30%, rgba(108,14,255,1) 100%);
  height: 100%;
  width: 100%;
  box-sizing: border-box;
`;

// Slide and Zoom Animation
const slideZoomAnimation = keyframes`
  0% {
    transform: translateX(0) scale(1.5); // Start position and scale (150% zoomed in)
  }
  50% {
    transform: translateX(-50%) scale(1.5); // Keep the zoom in while sliding to the left
  }
  100% {
    transform: translateX(-100%) scale(1.5); // End position with the same scale
  }
`;


// Sliding Background Stars Component
const SlidingBackgroundStars = () => {
  const randombgs = [
    "/images/wallpaper.png",
    "/images/wallpaper1.png",
    "/images/wallpaper2.png",
    "/images/wallpaper3.png"
  ]; // Array of background images

  const [backgroundImage, setBackgroundImage] = useState(randombgs[0]); // Start with the first image
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const changeBackground = () => {
      // Update index to the next wallpaper, looping back if needed
      setCurrentIndex(prevIndex => (prevIndex + 1) % randombgs.length);
    };

    // Start the interval for changing backgrounds every 30 seconds
    const interval = setInterval(() => {
      changeBackground();
    }, 30000); // Change every 30 seconds

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [randombgs]); // Include randombgs in dependencies

  // Update background image when currentIndex changes
  useEffect(() => {
    setBackgroundImage(randombgs[currentIndex]);
  }, [currentIndex]);

  return (
      <StyledBackground backgroundImage={backgroundImage} />
  );
};

// Styled Background Component
const StyledBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url(${props => props.backgroundImage});
  background-size: 150%; // Start zoomed in
  background-position: center;
  background-repeat: no-repeat;
  z-index: -1; // Ensure it's behind other components
  animation: ${slideZoomAnimation} 60s linear infinite; // Apply the slide animation over a longer duration
`;



// Taskbar Component
const Taskbar = styled.div`
  background: #3a3a3a;  // Dark taskbar
  height: 40px;
  width: 100%;
  position: absolute;
  bottom: 0;
  display: flex;
  align-items: center;
  padding: 0 10px;
  box-sizing: border-box;
  box-shadow: inset 1px 1px #fff, inset -1px -1px #888;
`;

// Styled React95 Button Component
const StyledReact95Button = styled(React95Button)`
  && {
    margin-right: 8px;
    font-family: 'OMORI_GAME', monospace;
    font-size: 16px;
    height: 30px;
    background-color: #3a3a3a;  // Dark button background
    border-color: #3a3a3a;  // Dark button border
    box-shadow: 1px 1px #888, -1px -1px #fff;

    ${({ isActive }) =>
        isActive &&
        `
        background-color: #5a5a5a;  // Active button background
        border-color: #5a5a5a;
        box-shadow: inset 2px 2px #888, inset -2px -2px #fff;
      `}

    &:focus {
      outline: none;
    }
  }
`;

// Clock Component
const Clock = styled.div`
  margin-left: auto;
  font-family: 'OMORI_GAME', monospace;
  font-size: 16px;
  color: #ffffff;  // Light clock text
  text-align: center;
  padding: 2px 5px;
