// App Component
const App = () => {
  const environmentRef = useRef(null);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString());
  const [isStartMenuVisible, setStartMenuVisible] = useState(false);
  const [zIndexCounter, setZIndexCounter] = useState(1);
  const [activeWindow, setActiveWindow] = useState('');
  const [windows, setWindows] = useState({
    aboutMe: { isVisible: false, position: { x: 100, y: 100 }, zIndex: 1 },
    extraWindow: { isVisible: false, position: { x: 200, y: 150 }, zIndex: 1 },
  });
  const [bootScreenVisible, setBootScreenVisible] = useState(true);
  const [bootMessage, setBootMessage] = useState("Boot up your laptop.");
  const audioRef = useRef(new Audio('/sfx/boot.mp3'));
  const backgroundMusicRef = useRef(new Audio('/sfx/lostnfound.mp3'));
  const [contentOpacity, setContentOpacity] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date().toLocaleTimeString()), 1000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    audioRef.current.onended = () => {
      setBootScreenVisible(false);
      setContentOpacity(1);
      backgroundMusicRef.current.volume = 0.5;
      backgroundMusicRef.current.loop = true;
      backgroundMusicRef.current.play();
    };
  }, []);

  const toggleWindow = (windowName) => {
    const newZIndex = zIndexCounter + 2;
    const isVisible = !windows[windowName].isVisible;
    setZIndexCounter(newZIndex);

    setWindows(prevWindows => ({
      ...prevWindows,
      [windowName]: {
        ...prevWindows[windowName],
        isVisible: isVisible,
        zIndex: isVisible ? newZIndex : prevWindows[windowName].zIndex,
      },
    }));

    setActiveWindow(isVisible ? windowName : (activeWindow === windowName ? '' : activeWindow));
  };

  const startDrag = (windowName, e) => {
    e.preventDefault();
    const newZIndex = zIndexCounter + 1;
    setZIndexCounter(newZIndex);
    setActiveWindow(windowName);
    setWindows(prevWindows => ({
      ...prevWindows,
      [windowName]: {
        ...prevWindows[windowName],
        zIndex: newZIndex,
      },
    }));

    const initialX = e.clientX;
    const initialY = e.clientY;

    const onDrag = (moveEvent) => {
      const dx = moveEvent.clientX - initialX;
      const dy = moveEvent.clientY - initialY;

      setWindows((prevWindows) => ({
        ...prevWindows,
        [windowName]: {
          ...prevWindows[windowName],
          position: { x: windows[windowName].position.x + dx, y: windows[windowName].position.y + dy },
          zIndex: prevWindows[windowName].zIndex,
        },
      }));
    };

    const onDragEnd = () => {
      document.removeEventListener('mousemove', onDrag);
      document.removeEventListener('mouseup', onDragEnd);
    };

    document.addEventListener('mousemove', onDrag);
    document.addEventListener('mouseup', onDragEnd);
  };

  const handleBootScreenClick = () => {
    setBootMessage("You booted up your laptop.");
    audioRef.current.volume = 0.2;
    audioRef.current.play();
  };

  return (
      <ThemeProvider>
        <GlobalStyles />
        <StyledBackground backgroundImage={backgroundImage} /> {/* Place background here */}
        <BootScreen isVisible={bootVisible}>Booting up...</BootScreen>
        <ContentContainer opacity={contentOpacity}>
          <Desktop>
            <Taskbar>
              <StyledReact95Button onClick={() => openWindow('aboutMe')}>
                About Me
              </StyledReact95Button>
              <StyledReact95Button onClick={() => openWindow('myPortfolio')}>
                My Portfolio
              </StyledReact95Button>
              <Clock>{currentTime}</Clock>
            </Taskbar>
          </Desktop>
          <DraggableFrame isVisible={windows.aboutMe.isVisible} style={{ top: windows.aboutMe.position.y, left: windows.aboutMe.position.x, zIndex: windows.aboutMe.zIndex }}>
            <TitleBar>
              <div>About Me</div>
            </TitleBar>
            <AboutMeContent />
            <React95Button onClick={() => closeWindow('aboutMe')}>Close</React95Button>
          </DraggableFrame>
          <DraggableFrame isVisible={windows.myPortfolio.isVisible} style={{ top: windows.myPortfolio.position.y, left: windows.myPortfolio.position.x, zIndex: windows.myPortfolio.zIndex }}>
            <TitleBar>
              <div>My Portfolio</div>
            </TitleBar>
            <MyPortfolioContent />
            <React95Button onClick={() => closeWindow('myPortfolio')}>Close</React95Button>
          </DraggableFrame>
        </ContentContainer>
      </ThemeProvider>
  );
};
export default App;
