import React, { useState, useEffect, useRef } from 'react';
import { ThemeProvider, Frame, List, TitleBar, Button as React95Button } from '@react95/core';
import styled, { keyframes } from 'styled-components';
import GlobalStyles from './GlobalStyles';

// Boot Screen Component
const BootScreen = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: #1e1e1e; // Dark background
  color: #ffffff; // Light text
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: 'OMORI_GAME', monospace;
  font-size: 24px;
  z-index: 999;
  opacity: ${({ isVisible }) => (isVisible ? 1 : 0)};
  transition: opacity 2s ease-out;
  pointer-events: ${({ isVisible }) => (isVisible ? 'all' : 'none')};
`;

// Content Container Component
const ContentContainer = styled.div`
  background-color: #1e1e1e; // Dark background
  opacity: ${({ opacity }) => opacity};
  transition: opacity 2s ease-in;
  width: 100vw;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  visibility: ${({ opacity }) => (opacity > 0 ? 'visible' : 'hidden')};
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

// Desktop Component
const Desktop = styled.div`
  background-color: #1e1e1e; // Dark background
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

// Window Border Component
const WindowBorder = styled.div`
  padding: 8px;
  box-sizing: border-box;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 3px solid #606060; // Adjust as needed
  height: 90vh;
  width: 80vw;
  background-color: #2e2e2e; // Darker background for windows
`;

// Background Slide Animation
const slideBackgroundAnimation = keyframes`
  0% {
    background-position: 0% 0%;
  }
  100% {
    background-position: 100% 0%;
  }
`;

// Sliding Background Stars Component
const SlidingBackgroundStars = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  animation: ${slideBackgroundAnimation} 30s linear infinite; // 30 seconds
  background-image: url(${({ backgroundImage }) => backgroundImage});
  background-size: 120%;
  background-repeat: repeat;
  image-rendering: high-quality;
  opacity: 1;
  overflow: hidden;
  z-index: 0;
`;

// Taskbar Component
const Taskbar = styled.div`
  background: #3a3a3a; // Dark taskbar
  height: 40px;
  width: 100%;
  position: absolute;
  bottom: 0;
  display: flex;
  align-items: center;
  padding: 0 10px;
  box-sizing: border-box;
  box-shadow: inset 1px 1px #fff, inset -1px -1px #888;
`;

// Styled React95 Button Component
const StyledReact95Button = styled(React95Button)`
  margin-right: 8px;
  font-family: 'OMORI_GAME', monospace;
  font-size: 16px;
  height: 30px;
  background-color: #3a3a3a; // Dark button background
  border-color: #3a3a3a; // Dark button border
  box-shadow: 1px 1px #888, -1px -1px #fff;

  ${({ isActive }) =>
      isActive &&
      `
    background-color: #5a5a5a; // Active button background
    border-color: #5a5a5a;
    box-shadow: inset 2px 2px #888, inset -2px -2px #fff;
  `}

  &:focus {
    outline: none;
  }
`;

// Clock Component
const Clock = styled.div`
  margin-left: auto;
  font-family: 'OMORI_GAME', monospace;
  font-size: 16px;
  color: #ffffff; // Light clock text
  text-align: center;
  padding: 2px 5px;
  background: #3a3a3a; // Dark clock background
  box-shadow: inset 1px 1px #888, inset -1px -1px #fff;
  user-select: none;
`;

// Draggable Frame Component
const DraggableFrame = styled(Frame)`
  cursor: move;
  position: absolute;
  display: ${({ isVisible }) => (isVisible ? 'block' : 'none')};
`;

// About Me Content Component
const AboutMeContent = () => (
    <div>
      <h1 style={{ color: '#ffffff' }}>About Me</h1> Hey, names 이스터!<br />
      I'm that weird Korean nerd that produces music, watches anime,<br />
      sweats destiny 2, edits videos, draws, and codes (clearly).<br />
      Feel free to stay and chill here!
      <hr style={{ borderColor: '#555555' }} />
      <div style={{ display: 'flex', marginTop: '20px', alignItems: 'center', justifyContent: 'flex-start' }}>
        <a href="https://github.com/eastercreeper" target="_blank" rel="noopener noreferrer">
          <img src="/images/gh.png" alt="GitHub Logo" style={{ width: '15px', height: '15px', marginRight: '10px' }} />
        </a>
        <a href="https://github.com/eastercreeper" target="_blank" rel="noopener noreferrer">
          <img src="/images/yt.png" alt="YouTube Logo" style={{ width: '15px', height: '15px', marginRight: '10px' }} />
        </a>
      </div>
    </div>
);

// My Portfolio Content Component
const MyPortfolioContent = () => (
    <div>
      <h1 style={{ color: '#ffffff' }}>Extra Window</h1> {/* Light text */}
      <p style={{ color: '#ffffff' }}>Test</p> {/* Light text */}
    </div>
);

// App Component
const App = () => {
  const environmentRef = useRef(null);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString());
  const [isStartMenuVisible, setStartMenuVisible] = useState(false);
  const [zIndexCounter, setZIndexCounter] = useState(1);
  const [activeWindow, setActiveWindow] = useState('');
  const [windows, setWindows] = useState({
    aboutMe: { isVisible: false, position: { x: 100, y: 100 }, zIndex: 1 },
    extraWindow: { isVisible: false, position: { x: 200, y: 150 }, zIndex: 1 },
  });
  const [bootScreenVisible, setBootScreenVisible] = useState(true);
  const [bootMessage, setBootMessage] = useState("Boot up your laptop.");
  const audioRef = useRef(new Audio('/sfx/boot.mp3'));
  const backgroundMusicRef = useRef(new Audio('/sfx/lostnfound.mp3'));
  const [contentOpacity, setContentOpacity] = useState(0);

  // Array of wallpapers
  const wallpapers = [
    "/images/wallpaper1.png",
    "/images/wallpaper2.png",
    "/images/wallpaper3.png",
  ];

  const [currentWallpaper, setCurrentWallpaper] = useState(wallpapers[0]);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const wallpaperChangeInterval = setInterval(() => {
      setCurrentWallpaper((prevWallpaper) => {
        const currentIndex = wallpapers.indexOf(prevWallpaper);
        return wallpapers[(currentIndex + 1) % wallpapers.length];
      });
    }, 30000); // Change wallpaper every 30 seconds

    return () => clearInterval(wallpaperChangeInterval);
  }, []);

  useEffect(() => {
    audioRef.current.play().then(() => {
      // Handle successful playback
    }).catch((error) => {
      console.error("Error playing audio:", error);
    });
    backgroundMusicRef.current.loop = true;
    backgroundMusicRef.current.volume = 0.2;
    backgroundMusicRef.current.play().then(() => {
      // Handle successful playback
    }).catch((error) => {
      console.error("Error playing background music:", error);
    });
  }, []);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setBootScreenVisible(false);
      setContentOpacity(1);
      setTimeout(() => {
        setStartMenuVisible(false);
        setBootMessage("");
      }, 2000); // Delay before hiding boot message
    }, 3000); // 3 seconds for the boot screen

    return () => clearTimeout(timeout);
  }, []);

  const toggleStartMenu = () => {
    setStartMenuVisible(!isStartMenuVisible);
  };

  const handleWindowClick = (windowName) => {
    const updatedWindows = { ...windows };
    const activeWindowZIndex = updatedWindows[windowName].zIndex;

    // Set the clicked window to the highest zIndex
    for (const key in updatedWindows) {
      if (updatedWindows[key].zIndex <= activeWindowZIndex) {
        updatedWindows[key].zIndex++;
      }
    }
    updatedWindows[windowName].zIndex = 1;

    setWindows(updatedWindows);
    setActiveWindow(windowName);
  };

  const handleWindowClose = (windowName) => {
    setWindows((prev) => ({
      ...prev,
      [windowName]: { ...prev[windowName], isVisible: false },
    }));
  };

  const handleWindowDrag = (e, windowName) => {
    const updatedWindows = { ...windows };
    updatedWindows[windowName].position = {
      x: e.clientX - 100, // Offset to center the window
      y: e.clientY - 100, // Offset to center the window
    };
    setWindows(updatedWindows);
  };

  const handleRightClick = (e) => {
    e.preventDefault();
    toggleStartMenu();
  };

  return (
      <ThemeProvider>
        <GlobalStyles />
        <SlidingBackgroundStars backgroundImage={currentWallpaper} />
        <BootScreen isVisible={bootScreenVisible}>
          <div>{bootMessage}</div>
        </BootScreen>
        <ContentContainer opacity={contentOpacity}>
          <Desktop onContextMenu={handleRightClick}>
            <WindowBorder>
              <h1 style={{ color: '#ffffff' }}>Welcome to the Desktop!</h1> {/* Light text */}
            </WindowBorder>
            {Object.entries(windows).map(([windowName, windowProps]) => (
                windowProps.isVisible && (
                    <DraggableFrame
                        key={windowName}
                        isVisible={windowProps.isVisible}
                        onMouseDown={() => handleWindowClick(windowName)}
                        onDrag={(e) => handleWindowDrag(e, windowName)}
                        style={{ left: windowProps.position.x, top: windowProps.position.y, zIndex: windowProps.zIndex }}
                        draggable
                    >
                      <div style={{ padding: '20px' }}>
                        {windowName === 'aboutMe' && <AboutMeContent />}
                        {windowName === 'extraWindow' && <MyPortfolioContent />}
                        <button onClick={() => handleWindowClose(windowName)}>Close</button>
                      </div>
                    </DraggableFrame>
                )
            ))}
          </Desktop>
          <Taskbar>
            <StyledReact95Button onClick={() => setWindows((prev) => ({ ...prev, aboutMe: { ...prev.aboutMe, isVisible: !prev.aboutMe.isVisible } }))}>
              About Me
            </StyledReact95Button>
            <StyledReact95Button onClick={() => setWindows((prev) => ({ ...prev, extraWindow: { ...prev.extraWindow, isVisible: !prev.extraWindow.isVisible } }))}>
              My Portfolio
            </StyledReact95Button>
            <Clock>{currentTime}</Clock>
          </Taskbar>
        </ContentContainer>
      </ThemeProvider>
  );
};

export default App;
